package com.andersen.servlet;import javax.servlet.ServletException;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import java.io.IOException;import java.sql.*;import static java.lang.Class.*;public class ListServlet extends HttpServlet {    @Override    protected void doGet(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        try {            Class.forName("com.mysql.cj.jdbc.Driver");            Connection connection = DriverManager.getConnection("jdbc:mysql://127.0.0.1:3306/mybatis","root",                    "175749479");            String sql = "select ID,COMMAND,DESCRIPTION,CONTENT from message";            PreparedStatement statement = connection.prepareStatement(sql);            ResultSet rs = statement.executeQuery();            while(rs.next()){                System.out.println(rs.getString("DESCRIPTION"));            }        } catch (ClassNotFoundException e) {            e.printStackTrace();        } catch (SQLException e) {            e.printStackTrace();        }    }    @Override    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {        this.doGet(req,resp);    }}