package com.andersen.springbootsty;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.*;import java.math.BigDecimal;@RestControllerpublic class HelloCtr {    @Autowired    private LimitConfig limitConfig;    @GetMapping({"/hi","/hello"})    public String hello(){        return "你好哇世界"+limitConfig.getMinMoney()+",说明："+limitConfig.getMaxMoney();    }    @RequestMapping("/idx")    /*@GetMapping是get方法,requestmapping都可获取*/    public String idx(){        /*正常使用@RestController注解返回数据，若要解析模版，则改为@Controller注解*/        return "index";    }    @GetMapping("/getparma")/*接收参数*/    public String getParma(@RequestParam("id") Integer id){        return "参数是"+id;    }}